mapper.select:
select *
from tbl_dictionary_value v
         LEFT JOIN tbl_dictionary_type t on v.typeCode = t.code order by  v.typeCode,v.orderNo;

select *
from tbl_dictionary_value v
         LEFT JOIN tbl_dictionary_type t on v.typeCode = t.code where v.id=#{id};

mapper.update:
update tbl_dictionary_value set
    value=#{value},
    text=#{text},
    orderNo=#{orderNo},
    typeCode=#{typeCode}
where id=#{id}
